CREATE FUNCTION f_organigramas(OUT padre_id integer, OUT id integer, OUT unidad_administrativa character varying, OUT enlace text) RETURNS SETOF record
    LANGUAGE sql
    AS $$
/**********************************************************************************
 Nombre: f_organigramas
 Descripción: Función para obtener el listado de unidades administrativas con los enlaces correspondientes. 
 Historial de Modificaciones:
   Ver        Fecha         Autor           Descripción
  ---------  ----------  ---------------  ---------------------------------------
    1.0      20/10/2014  Javier Loza     Creación de la función.
***********************************************************************************/
WITH RECURSIVE arbol (padre_id, id) AS (  
    SELECT  
    n.padre_id,  
    n.id,
    n.unidad_administrativa,
    case when ne.nivel_estructural like  'GERENCIA EJECUTIVA' then CONCAT(CONCAT('<u>',n.unidad_administrativa),'</u>')
    when ne.nivel_estructural like  'GERENCIA' then CONCAT(CONCAT('<u>',n.unidad_administrativa),'</u>')
    when ne.nivel_estructural like  'DEPARTAMENTO' then CONCAT(CONCAT('<li>',n.unidad_administrativa),'</li>')
    else '' end as enlace
    FROM  
    organigramas n  
    inner join nivelestructurales ne on n.nivel_estructural_id=ne.id
  UNION ALL  
    SELECT n2.padre_id, n2.id,n2.unidad_administrativa,
    case when nes.nivel_estructural like  'GERENCIA EJECUTIVA' then CONCAT(CONCAT('<u>',n2.unidad_administrativa),'</u>')
    when nes.nivel_estructural like  'GERENCIA' then CONCAT(CONCAT('<u>',n2.unidad_administrativa),'</u>')
    when nes.nivel_estructural like  'DEPARTAMENTO' then CONCAT(CONCAT('<li>',n2.unidad_administrativa),'</li>')
    else '' end as enlace
    FROM arbol a 
    INNER JOIN organigramas n2  
      ON (n2.id = a.padre_id)  
    INNER JOIN nivelestructurales nes on n2.nivel_estructural_id=nes.id
  )  
SELECT distinct * FROM arbol
where padre_id is not null
order by padre_id,id;


$$;


