CREATE FUNCTION f_relaborales_movilidad(OUT id_relaboral integer, OUT id_relaboralmovilidad integer, OUT id_gerencia_administrativa integer, OUT gerencia_administrativa character varying, OUT id_departamento_administrativo integer, OUT departamento_administrativo character varying, OUT id_organigrama integer, OUT unidad_administrativa character varying, OUT organigrama_sigla character varying, OUT organigrama_orden integer, OUT id_area integer, OUT area character varying, OUT id_ubicacion integer, OUT ubicacion character varying, OUT numero integer, OUT cargo character varying, OUT evento_id integer, OUT evento character varying, OUT motivo character varying, OUT id_pais integer, OUT pais character varying, OUT id_departamento integer, OUT departamento character varying, OUT lugar character varying, OUT fecha_ini date, OUT hora_ini time without time zone, OUT fecha_fin date, OUT hora_fin time without time zone, OUT id_memorandum integer, OUT id_tipomemorandum integer, OUT tipo_memorandum character varying, OUT memorandum_correlativo character varying, OUT memorandum_gestion integer, OUT fecha_mem timestamp without time zone, OUT observacion character varying, OUT estado integer, OUT estado_descripcion character varying) RETURNS SETOF record
    LANGUAGE sql
    AS $$
/**********************************************************************************
 Nombre: f_relaborales_movilidad
 Descripción: Función para obtener los registros de movilidad funcionaria de acuerdo a los registro de relación laboral válidos. 
 Historial de Modificaciones:
   Ver        Fecha         Autor           Descripción
  ---------  ----------  ---------------  ---------------------------------------
     1.0      17/11/2014  Javier Loza     Creación de la función.    
***********************************************************************************/
select r.id as id_relaboral,
rm.id as id_relaboralmovilidad,
case when 
tor.unidad_administrativa is not null
then 
case when o.unidad_administrativa like 'GERENCIA%'
then o.id
else tor.id end 
else o.id 
end as id_gerencia_administrativa,
case when 
tor.unidad_administrativa is not null
then 
case when o.unidad_administrativa like 'GERENCIA%'
then o.unidad_administrativa
else tor.unidad_administrativa end 
else o.unidad_administrativa 
end as gerencia_administrativa,
case when tor.unidad_administrativa is not null
then 
case when o.unidad_administrativa like 'GERENCIA%'
then null
else o.id end
else null 
end as id_departamento_administrativo,
case when tor.unidad_administrativa is not null
then 
case when o.unidad_administrativa like 'GERENCIA%'
then ''
else o.unidad_administrativa end
else '' 
end as departamento_administrativo,
o.id as id_organigrama,
o.unidad_administrativa,
o.sigla as organigrama_sigla,
o.orden as organigrama_orden,
tempareas.id as id_area,
tempareas.unidad_administrativa as area,
u.id as id_ubicacion,
u.ubicacion,
rm.numero,
rm.cargo,
rm.evento_id,
cast('' as character varying) as evento,
rm.motivo,
rm.pais_id,
ps.pais,
rm.departamento_id,
ds.departamento,
rm.lugar,
rm.fecha_ini,
rm.hora_ini,
rm.fecha_fin,
rm.hora_fin,
m.id as id_memorandum,
tm.id as id_tipomemorandum,
tm.tipo_memorandum,
m.correlativo as memorandum_correlativo,
m.gestion as memorandum_gestion,
m.fecha_mem,
rm.observacion,
rm.estado,
case when rm.estado=1 then 'ACTIVO'
when rm.estado=0 then 'PASIVO'
else '' end as estado_descripcion
from relaboralesmovilidades rm
inner join relaborales r on r.id = rm.relaboral_id
inner join das d on d.id=rm.da_id
inner join regionales rg on rm.regional_id=rg.id
inner join departamentos dp on dp.id=rg.departamento_id
left join provincias p on rg.provincia_id=p.id
left join localidades l on rg.localidad_id=l.id
left join organigramas o on rm.organigrama_id=o.id
left join organigramas tor on tor.id = o.padre_id
left join ubicaciones u on u.id = rm.ubicacion_id
left join organigramas tempareas on tempareas.id = rm.area_id
inner join modalidadmemorandum mm on mm.id = rm.modalidadmemorandum_id 
inner join tiposmemorandums tm on tm.id = mm.tipomemorandum_id
inner join memorandums m on m.id = rm.memorandum_id and m.tipomemorandum_id = tm.id
left join paises ps on ps.id = rm.pais_id
left join departamentos ds on ds.id = rm.departamento_id
order by rm.numero,m.fecha_mem;
$$;


